import styled from "styled-components";

export const Container = styled.div`
	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;
	min-height: 100vh;
	width: 100%;
`;

export const Logo = styled.div`
	.logo-container {
		display: flex;
		flex-direction: column;
		align-items: center;
	}
	.logo-image {
		height: 100px;
		width: auto;
	}
`;

export const Texto = styled.p`
	padding: 0;
	display: flex;
	color: black;
	color: ${(props) => (props.verde ? "#87d3b8" : "bl∫ack")};
`;
